// src/App.js
import React from 'react';
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import AlertCard from './components/AlertCard';
import MetricCard from './components/MetricCard';
import './styles/App.css'; // Import the global styles

/**
 * Main application component rendering the complete Water Intelligence Dashboard.
 */
const App = () => {
  // Data for the Alerts section
  const alertsData = [
    {
      type: 'warning',
      title: 'HEAVY METAL DETECTED',
      message: 'Lead concentration approaching EPA limit - immediate investigation recommended',
      location: 'bathroom â€¢ 1/10/2025, 6:10:00 PM'
    },
    {
      type: 'warning',
      title: 'TDS ANOMALY',
      message: 'TDS level exceeded safe threshold - water quality degradation detected',
      location: 'bathroom â€¢ 1/10/2025, 6:00:00 PM'
    },
    {
      type: 'danger',
      title: 'LEAK DETECTED',
      message: 'Sudden pressure drop detected in kitchen sensor - potential pipe leak',
      location: 'kitchen â€¢ 1/10/2025, 3:45:00 PM'
    }
  ];

  // Data for the Metric section
  const metricsData = [
    {
      title: 'FLOW RATE',
      value: '24.6',
      unit: 'L/min',
      type: 'flow-rate',
      icon: 'ðŸ’§',
      subText: 'Current flow'
    },
    {
      title: 'PRESSURE',
      value: '45.8',
      unit: 'PSI',
      type: 'pressure',
      icon: ' tlak',
      subText: 'System pressure'
    },
    {
      title: 'TOTAL USAGE',
      value: '1385',
      unit: 'Liters',
      type: 'total-usage',
      icon: 'ðŸ“ˆ',
      subText: "Today's consumption"
    },
    {
      title: 'LEAK DETECTION',
      value: '1',
      unit: 'alerts',
      type: 'leak-detection',
      icon: 'ðŸš¨',
      isAlert: true
    }
  ];

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="main-content">
        <Header />
        
        {/* --- Alerts Section --- */}
        <section className="alerts">
          {alertsData.map((alert, index) => (
            <AlertCard
              key={index}
              type={alert.type}
              title={alert.title}
              message={alert.message}
              location={alert.location}
            />
          ))}
        </section>

        {/* --- Metrics Section --- */}
        <div className="metric-row">
          {metricsData.map((metric, index) => (
            <MetricCard
              key={index}
              title={metric.title}
              value={metric.value}
              unit={metric.unit}
              type={metric.type}
              icon={metric.icon}
              subText={metric.subText}
              isAlert={metric.isAlert}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
