// src/pages/ConsumptionPage.js
import React, { useState, useEffect,useRef } from 'react';
/**
 * Renders the full Consumption Analytics page content.
 */
const ConsumptionPage = () => {
    // ... (consumptionMetrics and leakReport data remain the same) ...
    const consumptionMetrics = [
        { title: 'TOTAL CONSUMPTION', value: '1385', unit: 'Liters', subText: '1.0% vs last hour', icon: 'üíß', type: 'total' },
        { title: 'AVERAGE FLOW', value: '21.9', unit: 'L/min', subText: 'Mean flow rate', icon: 'üåä', type: 'average' },
        { title: 'ACTIVE SENSORS', value: '3', unit: 'locations', subText: 'Monitoring points', icon: 'üìä', type: 'active' },
    ];

    const leakReport = {
        location: 'kitchen',
        flowRate: '8.5 L/min',
        pressure: '28.3 PSI',
        timestamp: 'Jan 10, 2025, 15:30'
    };
    
    // **CONSUMPTION CHART PLACEHOLDER WITH HOVER INTERACTIVITY**
    // **CONSUMPTION CHART PLACEHOLDER WITH HOVER INTERACTIVITY AND ANIMATION**
    const ConsumptionChartPlaceholder = () => {
        const [hoverData, setHoverData] = useState(null); 
        const [isAnimating, setIsAnimating] = useState(true); // <-- NEW state for animation
        const [pathLength, setPathLength] = useState(0); // <-- NEW state for actual path length
        const pathRef = useRef(null); // <-- NEW ref for the SVG path element

       // --- useEffect to measure path length and trigger animation ---
        useEffect(() => {
            // 1. MEASURE PATH: Get the actual length of the SVG path
            if (pathRef.current) {
                const length = pathRef.current.getTotalLength();
                setPathLength(length);
            }

            // 2. TRIGGER ANIMATION: Set isAnimating to false after a small delay
            const timer = setTimeout(() => {
                setIsAnimating(false);
            }, 100); 

            return () => clearTimeout(timer);
        }, []);

        // --- NEW DIMENSIONS for WIDE, SCROLLABLE SVG ---
        const width = 800; // MUST match CSS width for scrolling effect
        const height = 300; 
        const padding = 35; 
        const innerWidth = width - 2 * padding;
        const innerHeight = height - 2 * padding;

        const toX = (x) => padding + (x / 100) * innerWidth;
        const toY = (y) => padding + innerHeight - (y / 32) * innerHeight;
        
        // Inverse function: converts pixel Y to consumption value (0-32 range)
        const toValue = (y_coord) => (innerHeight - (y_coord - padding)) * 32 / innerHeight;

        // The key X-points and their Consumption (Y) values (0-32 scale)
        const curvePoints = [
            { x: 0, y: 25, time: '13:30' },
            { x: 20, y: 23, time: '14:30' },
            { x: 50, y: 8.5, time: '15:30' },
            { x: 80, y: 30, time: '16:30' }, // Adjusted to 80% to match visual
            { x: 100, y: 24, time: '17:30' }
        ];

        // The SVG path strings (unchanged from last correct visual update)
        const d_line = `M ${toX(0)}, ${toY(25)} 
                        C ${toX(15)}, ${toY(22)}, ${toX(30)}, ${toY(15)}, ${toX(50)}, ${toY(8.5)}
                        C ${toX(60)}, ${toY(10)}, ${toX(70)}, ${toY(28)}, ${toX(80)}, ${toY(30)}
                        C ${toX(90)}, ${toY(28)}, ${toX(95)}, ${toY(26)}, ${toX(100)}, ${toY(24)}`;
        
        const d_area = `${d_line} L ${toX(100)},${toY(0)} L ${toX(0)},${toY(0)} Z`;

        // X-Axis Labels Data
        const xAxisLabels = [
            { label: '13:30', xPercent: 0 },
            { label: '14:30', xPercent: 20 },
            { label: '15:30', xPercent: 50 },
            { label: '16:30', xPercent: 80 }, // Using 80% to align with the peak
            { label: '17:30', xPercent: 100 }
        ];

        // --- HOVER LOGIC ---
        const handleMouseMove = (e) => {
            const svgRect = e.currentTarget.getBoundingClientRect();
            // X coordinate within the SVG (0 to 800)
            const mouseX = e.clientX - svgRect.left; 

            // Simple method: find the closest x-coordinate from the fixed points.
            // In a real app, this would use a complex point-in-curve calculation.
            let closestPoint = null;
            let minDistance = Infinity;

            curvePoints.forEach(point => {
                const xCoord = toX(point.x);
                const distance = Math.abs(mouseX - xCoord);
                
                if (distance < minDistance) {
                    minDistance = distance;
                    closestPoint = point;
                }
            });

            if (closestPoint) {
                const xPixel = toX(closestPoint.x);
                const yPixel = toY(closestPoint.y);
                
                setHoverData({
                    x: xPixel,
                    y: yPixel,
                    time: closestPoint.time,
                    value: closestPoint.y.toFixed(1)
                });
            }
        };

        const handleMouseLeave = () => {
            setHoverData(null);
        };

        return (
            <div className="chart-placeholder">
                <p className="chart-title">24-Hour Consumption Pattern</p>
                <div className="chart-area-svg">
                    <svg 
                        viewBox={`0 0 ${width} ${height}`} 
                        style={{ width: `${width}px`, height: `${height}px` }}
                        onMouseMove={handleMouseMove} // Mouse event listener
                        onMouseLeave={handleMouseLeave}
                    > 
                        
                        {/* --- Y-Axis Labels & Grid Lines --- */}
                        {[0, 8, 16, 24, 32].map((value) => {
                            const y = toY(value);
                            return (
                                <g key={value}>
                                    {/* Grid Line (stretching across the wide graph) */}
                                    {value > 0 && value < 32 && (
                                        <line x1={padding} y1={y} x2={width - padding} y2={y} stroke="#ddd" strokeWidth="0.5" strokeDasharray="2 2" />
                                    )}
                                    {/* Y Label (positioned on the left) */}
                                    <text x={padding - 8} y={y + 3} textAnchor="end" fontSize="11" fill="#777">{value}</text>
                                </g>
                            );
                        })}
                        
                        {/* X-Axis Line (The bottom baseline, stretching across the wide graph) */}
                        <line x1={padding} y1={toY(0)} x2={width - padding} y2={toY(0)} stroke="#ccc" strokeWidth="1" />
                        
                        {/* --- X-Axis Labels (INSIDE GRAPH) --- */}
                        {xAxisLabels.map((item) => (
                            <text 
                                key={item.label} 
                                x={toX(item.xPercent)} 
                                y={toY(0) + 12} 
                                textAnchor="middle" 
                                fontSize="11" 
                                fill="#777"
                            >
                                {item.label}
                            </text>
                        ))}
                        
                        {/* --- The Area Fill and Data Line --- */}
                        <path d={d_area} fill="rgba(0, 123, 255, 0.15)" stroke="none" />
                        <path d={d_line} fill="none" stroke="#007bff" strokeWidth="2" />

                        {/* --- HOVER ELEMENTS (Crosshair and Tooltip) --- */}
                        {hoverData && (
                            <g>
                                {/* Crosshair Line (Vertical) */}
                                <line 
                                    x1={hoverData.x} 
                                    y1={toY(0)} 
                                    x2={hoverData.x} 
                                    y2={toY(32)} 
                                    stroke="#333" 
                                    strokeWidth="1" 
                                    strokeDasharray="4 4" 
                                />

                                {/* Data Circle */}
                                <circle 
                                    cx={hoverData.x} 
                                    cy={hoverData.y} 
                                    r="4" 
                                    fill="#007bff" 
                                    stroke="#fff" 
                                    strokeWidth="2" 
                                />

                                {/* Tooltip Background and Text */}
                                <rect 
                                    x={hoverData.x + 10} 
                                    y={hoverData.y - 25} 
                                    width="90" 
                                    height="30" 
                                    fill="#333" 
                                    rx="4" 
                                    ry="4"
                                />
                                <text 
                                    x={hoverData.x + 15} 
                                    y={hoverData.y - 15} 
                                    fontSize="10" 
                                    fill="#fff"
                                >
                                    {hoverData.time}
                                </text>
                                <text 
                                    x={hoverData.x + 15} 
                                    y={hoverData.y - 5} 
                                    fontSize="10" 
                                    fill="#fff"
                                >
                                    {hoverData.value} Liters
                                </text>
                            </g>
                        )}
                    </svg>
                </div>
            </div>
        );
    };


    return (
        <div className="consumption-page">
            <header className="dashboard-header">
                <div>
                    <h1>Consumption Analytics</h1>
                    <p>Monitor usage patterns and detect anomalies</p>
                </div>
            </header>

            {/* Top Metric Cards */}
            <div className="metric-row consumption-metrics">
                {consumptionMetrics.map((metric, index) => (
                    <div className={`metric-card ${metric.type}`} key={index}>
                        <div className="metric-card-header">
                            <p className="metric-title">{metric.title}</p>
                            <div className="icon-box">{metric.icon}</div>
                        </div>
                        <p className="metric-value">{metric.value} <span className="metric-unit">{metric.unit}</span></p>
                        <p className="metric-subtext">{metric.subText}</p>
                    </div>
                ))}
            </div>

            {/* Chart and Report Row */}
            <div className="chart-report-row">
                <div className="consumption-chart-box">
                    <ConsumptionChartPlaceholder />
                </div>
                
                <div className="leak-detection-report">
                    <p className="report-header">
                        <span className="title-icon">‚ö†Ô∏è</span> Leak Detection Report
                    </p>
                    <div className="report-card">
                        <span className="location-icon">üìç</span>
                        <p className="report-location">{leakReport.location}</p>
                        <p className="report-detail">Flow Rate: **{leakReport.flowRate}**</p>
                        <p className="report-detail">Pressure: **{leakReport.pressure}**</p>
                        <p className="report-date">{leakReport.timestamp}</p>
                        <button className="close-btn">√ó</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ConsumptionPage;
