/**
 * Renders the navigation sidebar with links and user profile.
 */
const Sidebar = () => {
  const navItems = [
    { name: 'Dashboard', icon: DashboardIcon, active: true },
    { name: 'Consumption', icon: ConsumptionIcon, active: false },
    { name: 'Quality Monitor', icon: QualityIcon, active: false },
    { name: 'Alerts', icon: AlertsIcon, active: false },
    { name: 'Settings', icon: SettingsIcon, active: false },
  ];

  return (
    <div className="sidebar">
      <div>
        <div className="sidebar-logo">
          {/* Placeholder for a logo image */}
          <span className='logo-icon'>ðŸ’§</span>
          <h3>HydroSentinel</h3>
          <p style={{fontSize: '10px', color: '#999', margin: '0 0 0 5px'}}>IoT Water Intelligence</p>
        </div>
        <nav>
          <h4>NAVIGATION</h4>
          {navItems.map(item => (
            <div
              key={item.name}
              className={`nav-item ${item.active ? 'active' : ''}`}
            >
              <item.icon />
              {item.name}
            </div>
          ))}
        </nav>
      </div>
      <div className="user-profile">
        <div className="user-avatar">U</div>
        <div className="user-details">
          <p>Water Manager</p>
          <p>System Administrator</p>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
