import React, { useState, useEffect } from 'react';
import { ref, onValue } from "firebase/database";
import { database } from '../firebase-config';

import Header from './Header';
import AlertCard from './AlertCard';
import MetricCard from './MetricCard';
import SystemMonitorCard from './SystemMonitorCard';

const DB_PATH = "SensorData";

/**
 * Renders the primary Water Intelligence Dashboard view, fetching live data.
 */
const DashboardContent = () => {
  // Live sensor data
  const [liveMetrics, setLiveMetrics] = useState({
    flow_rate_lpm: 0.0,
    tds_ppm: 0,
    total_liters: 0.0,
    timestamp: 'N/A'
  });

  // Fetch data from Firebase Realtime Database
  useEffect(() => {
    const sensorRef = ref(database, DB_PATH);

    const unsubscribe = onValue(sensorRef, (snapshot) => {
      const data = snapshot.val();
      console.log("ðŸ”¥ Firebase Live Data:", data);

      if (data) {
        setLiveMetrics({
          flow_rate_lpm: parseFloat(data.Flow_L_per_min || 0).toFixed(2),
          tds_ppm: Math.round(data.TDS_ppm || 0),
          total_liters: parseFloat(data.Volume_mL || 0).toFixed(1),
          timestamp: new Date().toLocaleString(),
        });
      } else {
        console.log("No data found at /SensorData");
      }
    });

    return () => unsubscribe();
  }, []);

  // --- ALERTS SECTION ---
  const alertsData = [
    {
      id: 1,
      type: 'warning',
      title: 'HEAVY METAL DETECTED',
      message: 'Lead concentration approaching EPA limit - immediate investigation recommended',
      location: 'bathroom â€¢ 1/10/2025, 6:10:00 PM'
    },
    {
      id: 2,
      type: 'warning',
      title: 'TDS ANOMALY',
      message: 'TDS level exceeded safe threshold - water quality degradation detected',
      location: 'bathroom â€¢ 1/10/2025, 6:00:00 PM'
    },
    {
      id: 3,
      type: 'danger',
      title: 'LEAK DETECTED',
      message: 'Sudden pressure drop detected in kitchen sensor - potential pipe leak',
      location: 'kitchen â€¢ 1/10/2025, 3:45:00 PM'
    }
  ];

  // --- Determine status ---
  const tdsValue = liveMetrics.tds_ppm;
  const isTdsSafe = tdsValue < 500 && tdsValue > 0;
  const qualityStatus = isTdsSafe ? 'NORMAL' : 'WARNING';

  // --- Blink when TDS > 500 ---
  const shouldBlink = tdsValue > 500;

  // --- Data for child components ---
  const systemMonitorData = {
    flow: { value: liveMetrics.flow_rate_lpm, unit: 'L/min' },
    pressure: { value: '45.8', unit: 'PSI' },
    location: 'bathroom',
    status: qualityStatus
  };

  const waterQualityData = {
    status: qualityStatus,
    metric: 'TDS (Total Dissolved Solids)',
    value: `${liveMetrics.tds_ppm} ppm`
  };

  const topMetricsData = [
    { title: 'FLOW RATE', value: liveMetrics.flow_rate_lpm, unit: 'L/min', type: 'flow-rate', icon: 'ðŸ’§', subText: 'Current flow' },
    { title: 'PRESSURE', value: '45.8', unit: 'PSI', type: 'pressure', icon: ' tlak', subText: 'System pressure' },
    { title: 'TOTAL USAGE', value: liveMetrics.total_liters, unit: 'mL', type: 'total-usage', icon: 'ðŸ“ˆ', subText: "Total volume" },
    { title: 'LEAK DETECTION', value: '1', unit: 'alerts', type: 'leak-detection', icon: 'ðŸš¨', isAlert: true }
  ];

  return (
    <>
      <Header />

      {/* --- Alerts Section --- */}
      <section className="alerts">
        {alertsData.map((alert, index) => {
          const isTdsAlert = alert.title === "TDS ANOMALY";
          const cardClass = isTdsAlert && shouldBlink ? "blink-alert" : "";

          return (
            <div key={index} className={cardClass}>
              <AlertCard
                type={alert.type}
                title={alert.title}
                message={alert.message}
                location={alert.location}
              />
            </div>
          );
        })}
      </section>

      {/* --- System Monitor & Water Quality Section --- */}
      <section className="live-monitor-row">
        <SystemMonitorCard monitorData={systemMonitorData} />
        <div className={`water-quality-card ${shouldBlink ? "blink-alert" : ""}`}>
          <p className="quality-title">
            <span role="img" aria-label="water-quality">ðŸš°</span> Water Quality
          </p>
          <div className={`quality-status-box ${waterQualityData.status.toLowerCase()}`}>
            {waterQualityData.status}
          </div>
          <p className="quality-metric">{waterQualityData.metric}</p>
          <p className="quality-value">{waterQualityData.value}</p>
          <p style={{ fontSize: '0.7em', marginTop: '10px' }}>Last Updated: {liveMetrics.timestamp}</p>
        </div>
      </section>

      {/* --- Top Metrics Section --- */}
      <div className="metric-row top-metrics">
        {topMetricsData.map((metric, index) => (
          <MetricCard
            key={index}
            title={metric.title}
            value={metric.value}
            unit={metric.unit}
            type={metric.type}
            icon={metric.icon}
            subText={metric.subText}
            isAlert={metric.isAlert}
          />
        ))}
      </div>
    </>
  );
};

export default DashboardContent;
