               title.includes("TDS") ? '‚ùóÔ∏è' : 'üö®';

  return (
    <div className={`alert-card ${type}`}>
      <div className="content">
        <h3>
          <span className="title-icon">{icon}</span>
          {title}
        </h3>
        <p>{message}</p>
        <p className="location">Location: {location}</p>
      </div>
      <button className="close-btn">√ó</button>
    </div>
  );
};

