// src/App.js (UPDATED CODE)
import React, { useState } from 'react'; // <-- ADD useState
import Sidebar from './components/Sidebar';
import DashboardContent from './components/DashboardContent'; // <-- NEW: Dashboard content moved here
import ConsumptionPage from './pages/ConsumptionPage'; // <-- NEW IMPORT
import QualityMonitorPage from './pages/QualityMonitorPage';
import './styles/App.css';

/**
 * Main application component responsible for routing and global layout.
 */
const App = () => {
  // State to manage the active page, default is 'Dashboard'
  const [activePage, setActivePage] = useState('Dashboard'); // <-- NEW STATE

  // Function to determine which component to render
  const renderPage = () => {
    switch (activePage) {
      case 'Dashboard':
        return <DashboardContent />; // Renders the dashboard view
      case 'Consumption':
        return <ConsumptionPage />; // Renders the new consumption view
      default:
        return <DashboardContent />;
    }
  };

  return (
    <div className="dashboard-container">
      {/* Pass the active page and the setter function to Sidebar */}
      <Sidebar activePage={activePage} onNavigate={setActivePage} /> 
      <div className="main-content">
        {renderPage()} {/* Render the currently active page */}
      </div>
    </div>
  );
};

export default App;
