// src/components/DashboardContent.js (NEW FILE)
import React from 'react';
import Header from './Header';
import AlertCard from './AlertCard';
import MetricCard from './MetricCard';
import SystemMonitorCard from './SystemMonitorCard';

/**
 * Renders the primary Water Intelligence Dashboard view.
 */
const DashboardContent = () => {
    // --- Dashboard Data (Moved from App.js) ---
    const alertsData = [
        { type: 'warning', title: 'HEAVY METAL DETECTED', message: 'Lead concentration approaching EPA limit - immediate investigation recommended', location: 'bathroom â€¢ 1/10/2025, 6:10:00 PM' },
        { type: 'warning', title: 'TDS ANOMALY', message: 'TDS level exceeded safe threshold - water quality degradation detected', location: 'bathroom â€¢ 1/10/2025, 6:00:00 PM' },
        { type: 'danger', title: 'LEAK DETECTED', message: 'Sudden pressure drop detected in kitchen sensor - potential pipe leak', location: 'kitchen â€¢ 1/10/2025, 3:45:00 PM' }
    ];

    const systemMonitorData = {
        flow: { value: '24.6', unit: 'L/min' },
        pressure: { value: '45.8', unit: 'PSI' },
        location: 'bathroom',
        status: 'NORMAL'
    };

    const waterQualityData = {
        status: 'WARNING',
        metric: 'TDS (Total Dissolved Solids)',
        value: '0 ppm'
    };

    const topMetricsData = [
        { title: 'FLOW RATE', value: '24.6', unit: 'L/min', type: 'flow-rate', icon: 'ðŸ’§', subText: 'Current flow' },
        { title: 'PRESSURE', value: '45.8', unit: 'PSI', type: 'pressure', icon: ' tlak', subText: 'System pressure' },
        { title: 'TOTAL USAGE', value: '1385', unit: 'Liters', type: 'total-usage', icon: 'ðŸ“ˆ', subText: "Today's consumption" },
        { title: 'LEAK DETECTION', value: '1', unit: 'alerts', type: 'leak-detection', icon: 'ðŸš¨', isAlert: true }
    ];

    return (
        <>
            <Header />
            
            {/* --- Alerts Section --- */}
            <section className="alerts">
                {alertsData.map((alert, index) => (
                    <AlertCard
                        key={index}
                        type={alert.type}
                        title={alert.title}
                        message={alert.message}
                        location={alert.location}
                    />
                ))}
            </section>

            {/* --- System Monitor & Water Quality Section --- */}
            <section className="live-monitor-row">
                <SystemMonitorCard monitorData={systemMonitorData} />
                <div className="water-quality-card">
                    <p className="quality-title">
                        <span role="img" aria-label="water-quality">ðŸš°</span> Water Quality
                    </p>
                    <div className={`quality-status-box ${waterQualityData.status.toLowerCase()}`}>
                        {waterQualityData.status}
                    </div>
                    <p className="quality-metric">{waterQualityData.metric}</p>
                    <p className="quality-value">{waterQualityData.value}</p>
                </div>
            </section>

            {/* --- Top Metrics Section --- */}
            <div className="metric-row top-metrics"> 
                {topMetricsData.map((metric, index) => (
                    <MetricCard
                        key={index}
                        title={metric.title}
                        value={metric.value}
                        unit={metric.unit}
                        type={metric.type}
                        icon={metric.icon}
                        subText={metric.subText}
                        isAlert={metric.isAlert}
                    />
                ))}
            </div>
        </>
    );
};

export default DashboardContent;
