import React, { useState, useEffect } from 'react';
import { ref, query, orderByChild, limitToLast, onValue } from "firebase/database";
// Import your Firebase config instance
import { database } from '../firebase-config'; // Adjust path based on where you put firebase-config.js

import Header from './Header';
import AlertCard from './AlertCard';
import MetricCard from './MetricCard';
import SystemMonitorCard from './SystemMonitorCard';

// This MUST match the path from the ESP32 code
const DB_PATH = "SensorData"; 

/**
 * Renders the primary Water Intelligence Dashboard view, fetching live data.
 */
const DashboardContent = () => {
    // 1. STATE TO HOLD LIVE DATA (Set initial values to 0 or 'N/A')
    const [liveMetrics, setLiveMetrics] = useState({
        flow_rate_lpm: 0.0,
        tds_ppm: 0,
        total_liters: 0.0,
        timestamp: 'N/A'
    });

    // 2. DATA FETCHING EFFECT
    useEffect(() => {
        const latestReadingQuery = query(
            ref(database, DB_PATH),
            orderByChild('timestamp'), 
            limitToLast(1)            
        );

        const unsubscribe = onValue(latestReadingQuery, (snapshot) => {
            if (snapshot.exists()) {
                const data = snapshot.val();
                const latestKey = Object.keys(data)[0];
                const latestValues = data[latestKey];
                
                // Update the state with live values
                setLiveMetrics({
                    flow_rate_lpm: parseFloat(latestValues.flow_rate_lpm).toFixed(2),
                    tds_ppm: Math.round(latestValues.tds_ppm), // Round TDS for cleaner display
                    total_liters: parseFloat(latestValues.total_liters).toFixed(1),
                    timestamp: new Date(latestValues.timestamp).toLocaleString()
                });
            } else {
                console.log("No data available at path:", DB_PATH);
            }
        });

        return () => unsubscribe();
    }, []); 
    
    // --- STATIC DATA DEFINITION ---
    const alertsData = [
        { type: 'warning', title: 'HEAVY METAL DETECTED', message: 'Lead concentration approaching EPA limit - immediate investigation recommended', location: 'bathroom â€¢ 1/10/2025, 6:10:00 PM' },
        { type: 'warning', title: 'TDS ANOMALY', message: 'TDS level exceeded safe threshold - water quality degradation detected', location: 'bathroom â€¢ 1/10/2025, 6:00:00 PM' },
        { type: 'danger', title: 'LEAK DETECTED', message: 'Sudden pressure drop detected in kitchen sensor - potential pipe leak', location: 'kitchen â€¢ 1/10/2025, 3:45:00 PM' }
    ];

    // --- LOGIC TO DETERMINE WATER QUALITY STATUS ---
    // Note: TDS standard for drinking water is typically < 500 ppm
    const tdsValue = liveMetrics.tds_ppm;
    const isTdsSafe = tdsValue < 500 && tdsValue > 0; // Assuming 0 is a placeholder for no data/dry sensor
    const qualityStatus = isTdsSafe ? 'NORMAL' : 'WARNING'; 


    // 3. UPDATED DATA FOR CHILD COMPONENTS (Using liveMetrics state)
    
    // a. System Monitor Card (Update Flow Rate)
    const systemMonitorData = {
        flow: { value: liveMetrics.flow_rate_lpm, unit: 'L/min' }, // LIVE FLOW
        pressure: { value: '45.8', unit: 'PSI' }, // Static placeholder for pressure
        location: 'bathroom',
        status: qualityStatus // LIVE STATUS
    };

    // b. Water Quality Card (Update TDS Value and Status)
    const waterQualityData = {
        status: qualityStatus, // LIVE STATUS
        metric: 'TDS (Total Dissolved Solids)',
        value: `${liveMetrics.tds_ppm} ppm` // LIVE TDS
    };

    // c. Top Metrics (Update Flow Rate and Total Usage)
    const topMetricsData = [
        { title: 'FLOW RATE', value: liveMetrics.flow_rate_lpm, unit: 'L/min', type: 'flow-rate', icon: 'ðŸ’§', subText: 'Current flow' }, // LIVE FLOW
        { title: 'PRESSURE', value: '45.8', unit: 'PSI', type: 'pressure', icon: ' tlak', subText: 'System pressure' }, // Static
        { title: 'TOTAL USAGE', value: liveMetrics.total_liters, unit: 'Liters', type: 'total-usage', icon: 'ðŸ“ˆ', subText: "Total volume" }, // LIVE TOTAL LITERS
        { title: 'LEAK DETECTION', value: '1', unit: 'alerts', type: 'leak-detection', icon: 'ðŸš¨', isAlert: true }
    ];

    return (
        <>
            <Header />
            
            {/* The SensorData component is now redundant since this file does the fetching. 
                We remove it to avoid duplicate code, but the raw data is available 
                in the liveMetrics state.
            */}
            
            {/* --- Alerts Section --- */}
            <section className="alerts">
                {alertsData.map((alert, index) => (
                    <AlertCard
                        key={index}
                        type={alert.type}
                        title={alert.title}
                        message={alert.message}
                        location={alert.location}
                    />
                ))}
            </section>

            {/* --- System Monitor & Water Quality Section --- */}
            <section className="live-monitor-row">
                <SystemMonitorCard monitorData={systemMonitorData} />
                <div className="water-quality-card">
                    <p className="quality-title">
                        <span role="img" aria-label="water-quality">ðŸš°</span> Water Quality
                    </p>
                    <div className={`quality-status-box ${waterQualityData.status.toLowerCase()}`}>
                        {waterQualityData.status}
                    </div>
                    <p className="quality-metric">{waterQualityData.metric}</p>
                    <p className="quality-value">{waterQualityData.value}</p>
                    <p style={{fontSize: '0.7em', marginTop: '10px'}}>Last Updated: {liveMetrics.timestamp}</p>
                </div>
            </section>

            {/* --- Top Metrics Section --- */}
            <div className="metric-row top-metrics"> 
                {topMetricsData.map((metric, index) => (
                    <MetricCard
                        key={index}
                        title={metric.title}
                        value={metric.value}
                        unit={metric.unit}
                        type={metric.type}
                        icon={metric.icon}
                        subText={metric.subText}
                        isAlert={metric.isAlert}
                    />
                ))}
            </div>
        </>
    );
};

export default DashboardContent;
