import React, { useState, useEffect } from 'react';
import { ref, onValue } from "firebase/database";
import { database } from '../firebase-config';

import Header from './Header';
import AlertCard from './AlertCard';
import MetricCard from './MetricCard';
import SystemMonitorCard from './SystemMonitorCard';

const DB_PATH = "SensorData";

const DashboardContent = () => {
  const [liveMetrics, setLiveMetrics] = useState({
    flow_rate_lpm: 0.0,
    tds_ppm: 0,
    total_liters: 0.0,
    timestamp: 'N/A'
  });

  const [isLeakDetected, setIsLeakDetected] = useState(false);
  const [isTdsAlert, setIsTdsAlert] = useState(false);
  const [leakDetectedAt, setLeakDetectedAt] = useState(null);
  const [tdsDetectedAt, setTdsDetectedAt] = useState(null);

  useEffect(() => {
    const sensorRef = ref(database, DB_PATH);

    const unsubscribe = onValue(sensorRef, (snapshot) => {
      const data = snapshot.val();
      if (!data) return;

      const flowRate = parseFloat(data.Flow_L_per_min || 0);
      const tdsValue = parseFloat(data.TDS_ppm || 0);
      const volume = parseFloat(data.Volume_mL || 0);

      setLiveMetrics({
        flow_rate_lpm: flowRate.toFixed(2),
        tds_ppm: Math.round(tdsValue),
        total_liters: volume.toFixed(1),
        timestamp: new Date().toLocaleString(),
      });

      // --- Leak Detection ---
      if (flowRate >= 0.1 && flowRate <= 0.5) {
        if (!isLeakDetected) {
          setLeakDetectedAt(new Date().toLocaleString());
        }
        setIsLeakDetected(true);
      } else {
        setIsLeakDetected(false);
      }

      // --- TDS Anomaly ---
      if (tdsValue > 500) {
        if (!isTdsAlert) {
          setTdsDetectedAt(new Date().toLocaleString());
        }
        setIsTdsAlert(true);
      } else {
        setIsTdsAlert(false);
      }
    });

    return () => unsubscribe();
  }, [isLeakDetected, isTdsAlert]);

  const tdsValue = liveMetrics.tds_ppm;
  const isTdsSafe = tdsValue < 500 && tdsValue > 0;
  const qualityStatus = isTdsSafe ? 'NORMAL' : 'WARNING';

  const systemMonitorData = {
    flow: { value: liveMetrics.flow_rate_lpm, unit: 'L/min' },
    pressure: { value: '45.8', unit: 'PSI' },
    location: 'bathroom',
    status: qualityStatus
  };

  const waterQualityData = {
    status: qualityStatus,
    metric: 'TDS (Total Dissolved Solids)',
    value: `${liveMetrics.tds_ppm} ppm`
  };

  const alertsData = [
    {
      id: 1,
      type: 'warning',
      title: 'HEAVY METAL DETECTED',
      message: 'Lead concentration approaching EPA limit - immediate investigation recommended',
      location: 'bathroom ‚Ä¢ 1/10/2025, 6:10:00 PM'
    },
    {
      id: 2,
      type: 'warning',
      title: 'TDS ANOMALY',
      message: 'TDS level exceeded safe threshold - water quality degradation detected',
      location: tdsDetectedAt ? `Detected at ${tdsDetectedAt}` : 'No anomaly detected'
    },
    {
      id: 3,
      type: 'danger',
      title: 'LEAK DETECTED',
      message: 'Flow rate between 0.1 ‚Äì 0.5 L/min detected ‚Äî possible leak',
      location: leakDetectedAt ? `Detected at ${leakDetectedAt}` : 'No leak detected'
    }
  ];

  const topMetricsData = [
    { title: 'FLOW RATE', value: liveMetrics.flow_rate_lpm, unit: 'L/min', type: 'flow-rate', icon: 'üíß', subText: 'Current flow' },
    { title: 'PRESSURE', value: '45.8', unit: 'PSI', type: 'pressure', icon: 'üìè', subText: 'System pressure' },
    { title: 'TOTAL USAGE', value: liveMetrics.total_liters, unit: 'mL', type: 'total-usage', icon: 'üìà', subText: 'Total volume' },
    { title: 'LEAK DETECTION', value: isLeakDetected ? '1' : '0', unit: 'alerts', type: 'leak-detection', icon: 'üö®', isAlert: isLeakDetected }
  ];

  return (
    <>
      <Header />

      {/* --- Alerts Section --- */}
      <section className="alerts">
        {alertsData.map((alert, index) => {
          const isTdsAlertCard = alert.title === "TDS ANOMALY";
          const isLeakAlertCard = alert.title === "LEAK DETECTED";
          const shouldBlink =
            (isTdsAlertCard && isTdsAlert) || (isLeakAlertCard && isLeakDetected);

          return (
            <div key={index} className={shouldBlink ? "blink-alert alert-card" : "alert-card"}>
              <AlertCard
                type={alert.type}
                title={alert.title}
                message={alert.message}
                location={alert.location}
              />
              {shouldBlink && (
                <p className="timestamp">
                  {isLeakAlertCard
                    ? `Detected at: ${leakDetectedAt}`
                    : `Detected at: ${tdsDetectedAt}`}
                </p>
              )}
            </div>
          );
        })}
      </section>

      {/* --- System Monitor & Water Quality Section --- */}
      <section className="live-monitor-row">
        <SystemMonitorCard monitorData={systemMonitorData} />
        <div className={`water-quality-card ${isTdsAlert ? "blink-alert" : ""}`}>
          <p className="quality-title">
            <span role="img" aria-label="water-quality">üö∞</span> Water Quality
          </p>
          <div className={`quality-status-box ${waterQualityData.status.toLowerCase()}`}>
            {waterQualityData.status}
          </div>
          <p className="quality-metric">{waterQualityData.metric}</p>
          <p className="quality-value">{waterQualityData.value}</p>
          {isTdsAlert && (
            <p className="timestamp" style={{ marginTop: '6px', fontSize: '0.8em' }}>
              Detected at: {tdsDetectedAt}
            </p>
          )}
          <p style={{ fontSize: '0.7em', marginTop: '10px' }}>
            Last Updated: {liveMetrics.timestamp}
          </p>
        </div>
      </section>

      {/* --- Top Metrics Section --- */}
      <div className="metric-row top-metrics">
        {topMetricsData.map((metric, index) => (
          <MetricCard
            key={index}
            title={metric.title}
            value={metric.value}
            unit={metric.unit}
            type={metric.type}
            icon={metric.icon}
            subText={metric.subText}
            isAlert={metric.isAlert}
          />
        ))}
      </div>
    </>
  );
};

export default DashboardContent;
